#pragma GCC diagnostic pop

static const u32 nDmaTests = sizeof(dmaTests) / sizeof(*dmaTests);

static unsigned passes;
static unsigned totalResults;

static void runTest(struct TestDma* test) {
	int i;
	vu32* dma = &REG_DMA0SAD + test->dma * 3;
	dma[0] = (u32) &test->sad0[3];
	dma[1] = (u32) &test->dad0[3];

	irqEnable(IRQ_HBLANK);
	while (REG_VCOUNT >= 156) {
		IntrWait(1, 2);
	}
	dma[2] = test->control & ~0x04000000;

	switch (test->control & 0x30000000) {
	case 0x00000000:
		break;
	case 0x10000000:
		VBlankIntrWait();
		break;
	case 0x20000000:
		IntrWait(1, 2);
		break;
	}
	dma[2] = 0;
	irqDisable(IRQ_HBLANK);

	memcpy(dstI, srcI, sizeof(dstI));
	memcpy(dstE, srcE, sizeof(dstE));
	for (i = 0; i < 7; ++i) {
		dstI[i] += 0x100010;
		test->other[i] = dstI[i];
		dstE[i] += 0x100010;
	}

	dma[0] = (u32) &test->sad[3];
	dma[1] = (u32) &test->dad[3];

	irqEnable(IRQ_HBLANK);
	IntrWait(1, 2);
	dma[2] = test->control;

	switch (test->control & 0x30000000) {
	case 0x00000000:
		break;
	case 0x10000000:
		VBlankIntrWait();
		break;
	case 0x20000000:
		IntrWait(1, 2);
		break;
	}
	dma[2] = 0;
	irqDisable(IRQ_HBLANK);

	memcpy(test->expected, test->dad, sizeof(test->expected));
}

static void printResult(int offset, int line, const char* preface, u32 value, u32 expected) {
	static const int base = 96;
	if (offset > line || base + 32 * (line - offset) > 576) {
		return;
	}

	snprintf(&textGrid[base + 32 * (line - offset)], 31, "%-5s: %08lX", preface, value);
	if (value == expected) {
		strncpy(&textGrid[base + 32 * (line - offset) + 16], "PASS", 10);
	} else {
		snprintf(&textGrid[base + 32 * (line - offset) + 16], 16, "!= %08lX", expected);
	}
}

static void doResult(const char* preface, const char* testName, u32 value, u32 expected) {
	if (value != expected) {
		debugprintf("FAIL: %s %s", testName, preface);
		savprintf("%s: Got %08lX vs %08lX: FAIL", preface, value, expected);
	} else {
		debugprintf("PASS: %s %s", testName, preface);
		++passes;
	}
	++totalResults;
}

static void printResults(const char* preface, const struct TestDma* values, const struct TestDma* expected, int base) {
	snprintf(&textGrid[32], 31, "DMA test: %s", preface);
	if ((values->control & 0x00600000) == 0x00200000) {
		printResult(base, 0, "0", values->expected[0], expected->expected[0]);
		printResult(base, 1, "1", values->expected[1], expected->expected[1]);
		printResult(base, 2, "2", values->expected[2], expected->expected[2]);
	}
	printResult(base, 3, "3", values->expected[3], expected->expected[3]);
	if ((values->control & 0x00600000) == 0x00000000 || (values->control & 0x00600000) == 0x00600000) {
		printResult(base, 4, "4", values->expected[4], expected->expected[4]);
		printResult(base, 5, "5", values->expected[5], expected->expected[5]);
		printResult(base, 6, "6", values->expected[6], expected->expected[6]);
	}

	if (values->dad != values->dad0) {
		if ((values->control & 0x00600000) == 0x00200000) {
			printResult(base, 7, "Old 0", values->other[0], srcI[0] + 0x100010);
			printResult(base, 8, "Old 1", values->other[1], srcI[1] + 0x100010);
			printResult(base, 9, "Old 2", values->other[2], srcI[2] + 0x100010);
		}
		printResult(base, 10, "Old 3", values->other[3], srcI[3] + 0x100010);
		if ((values->control & 0x00600000) == 0x00000000 || (values->control & 0x00600000) == 0x00600000) {
			printResult(base, 11, "Old 4", values->other[4], srcI[4] + 0x100010);
			printResult(base, 12, "Old 5", values->other[5], srcI[5] + 0x100010);
			printResult(base, 13, "Old 6", values->other[6], srcI[6] + 0x100010);
		}
	}
}

static void runDmaSuite(void) {
	passes = 0;
	totalResults = 0;
	const struct DmaTest* activeTest = 0;
	int i;
	for (i = 0; i < nDmaTests; ++i) {
		struct TestDma currentTest = {0};
		activeTest = &dmaTests[i];
		memcpy(&currentTest, &activeTest->expected, sizeof(currentTest));
		runTest(&currentTest);

		savprintf("DMA test: %s", activeTest->testName);
		if ((activeTest->expected.control & 0x00600000) == 0x00200000) {
			doResult("0", activeTest->testName, currentTest.expected[0], activeTest->expected.expected[0]);
			doResult("1", activeTest->testName, currentTest.expected[1], activeTest->expected.expected[1]);
			doResult("2", activeTest->testName, currentTest.expected[2], activeTest->expected.expected[2]);
		}
		doResult("3", activeTest->testName, currentTest.expected[3], activeTest->expected.expected[3]);
		if ((activeTest->expected.control & 0x00600000) == 0x00000000 || (activeTest->expected.control & 0x00600000) == 0x00600000) {
			doResult("4", activeTest->testName, currentTest.expected[4], activeTest->expected.expected[4]);
			doResult("5", activeTest->testName, currentTest.expected[5], activeTest->expected.expected[5]);
			doResult("6", activeTest->testName, currentTest.expected[6], activeTest->expected.expected[6]);
		}

		if (currentTest.dad != dstI) {
			if ((activeTest->expected.control & 0x00600000) == 0x00200000) {
				doResult("Old 0", activeTest->testName, currentTest.other[0], srcI[0] + 0x100010);
				doResult("Old 1", activeTest->testName, currentTest.other[1], srcI[1] + 0x100010);
				doResult("Old 2", activeTest->testName, currentTest.other[2], srcI[2] + 0x100010);
			}
			doResult("Old 3", activeTest->testName, currentTest.other[3], srcI[3] + 0x100010);
			if ((activeTest->expected.control & 0x00600000) == 0x00000000 || (activeTest->expected.control & 0x00600000) == 0x00600000) {
				doResult("Old 4", activeTest->testName, currentTest.other[4], srcI[4] + 0x100010);
				doResult("Old 5", activeTest->testName, currentTest.other[5], srcI[5] + 0x100010);
				doResult("Old 6", activeTest->testName, currentTest.other[6], srcI[6] + 0x100010);
			}
		}
	}
}

static size_t listDmaSuite(const char** names, size_t size, size_t offset) {
	size_t i;
	for (i = 0; i < size; ++i) {
		if (i + offset >= nDmaTests) {
			break;
		}
		names[i] = dmaTests[i + offset].testName;
	}
	return i;
}

static void showDmaSuite(size_t index) {
	const struct DmaTest* activeTest = &dmaTests[index];
	struct TestDma currentTest = {0};
	size_t resultIndex = 0;
	while (1) {
		memset(&textGrid[GRID_STRIDE], 0, sizeof(textGrid) - GRID_STRIDE);
		scanKeys();
		u16 keys = keysDownRepeat();

		if (keys & KEY_B) {
			return;
		}
		memcpy(&currentTest, &activeTest->expected, sizeof(currentTest));
		runTest(&currentTest);
		printResults(activeTest->testName, &currentTest, &activeTest->expected, resultIndex);
		updateTextGrid();
	}
}

const struct TestSuite dmaTestSuite = {
	.name = "DMA tests",
	.run = runDmaSuite,
	.list = listDmaSuite,
	.show = showDmaSuite,
	.nTests = sizeof(dmaTests) / sizeof(*dmaTests),
	.passes = &passes,
	.totalResults = &totalResults
};
